import ListHeader from "./components/ListHeader";
import ListItem from "./components/ListItem";
import Auth from "./components/Auth";

import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";


function App() {
  const [cookies, setCookie, removeCookie] = useCookies(null);
  const authToken  = cookies.AuthToken;
  const userEmail = cookies.Email;
  const [tasks, setTasks] = useState(null);



  async function getData() {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/todos/${userEmail}`);
      const json = await response.json();
      setTasks(json);

    } catch (err) {
      console.error(err); 
    }
  }

  useEffect(() => {
    if(authToken) {
      getData();
    }
  }, [])

  console.log(tasks)

  // Sort ny date
  const sortedTasks = tasks?.sort((a, b) => new Date(a.date) - new Date(b.date));

  return (
    <div>
      {!authToken && <Auth />}
      {authToken &&
      <>
        <div class="navbar">
          <div class="navbar-logo">
            <img src="/static/images/Imagify_logo.png" alt="Logo" />
          </div>
          <button type="button" className="signout-btn" onClick={signOut}>
            <span class="button-text">Sign out</span>
            <span class="button-icon"><svg xmlns="http://www.w3.org/2000/svg" width="16" viewBox="0 0 512 512">
            <path d="M502.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-12.5-12.5-32.8-12.5-45.3 0s-12.5 
            32.8 0 45.3L402.7 224 192 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l210.7 0-73.4 73.4c-12.5 
            12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0l128-128zM160 96c17.7 0 32-14.3 32-32s-14.3-32-32-32L96 
            32C43 32 0 75 0 128L0 384c0 53 43 96 96 96l64 0c17.7 0 32-14.3 32-32s-14.3-32-32-32l-64 0c-17.7 
            0-32-14.3-32-32l0-256c0-17.7 14.3-32 32-32l64 0z"/></svg></span>
          </button>
          <div class="navbar-profile">
            <div class="profile-circle"></div>
          </div>
        </div>

        <div className="app">
          <ListHeader listName = {"To Do list"} getData={getData}/>
          <p className="welcome">Welcome back {userEmail}</p>
          {sortedTasks?.map((task) => <ListItem key={task.id} task={task} getData={getData} />)}
        </div>
      </>}  
    </div>
  );
}

export default App;
