import { useState } from "react";
import { useCookies } from 'react-cookie';

function Auth() {
  const[cookies,setCookie, removeCookie] = useCookies(null);

  const [isLogIn, setIsLogin] = useState(true);

  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirmPassword, setConfirmPassword] = useState(null);

  const [error, setError] = useState(null);

  console.log(cookies);

  function viewLogin(status) {
    setError(null);
    setIsLogin(status);
  }

  async function handleSubmit(e, endpoint) {
    e.preventDefault();

    if(!isLogIn && password !== confirmPassword) {
      setError('Make sure passwords match!');
      return
    }

    const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/${endpoint}`, {
      method: 'POST',
      headers: {"Content-Type": 'application/json'},
      body: JSON.stringify({email, password})
    });

    const data = await response.json();
    console.log(data);

    if(data.detail) {
      setError(data.detail);
    } else {
      setCookie('Email', data.email);
      setCookie('AuthToken', data.token);

      window.location.reload();
    }
  }

    return (
      <div className="bg-[#191919] h-screen flex flex-col items-center justify-center px-8 xs:px-4 sm:px-8 lg:px-4 xl:px-72 2xl:px-96 3xl:px-128 4xl:px-144">
        <div className="lex flex-col items-center justify-center xl:flex-row xl:justify-between w-full xl:w-[100%] 2xl:w-[100%]">
          <div className="flex flex-col items-center justify-center mb-4 xs:mb-6 sm:mb-8 lg:mb-10 xl:mb-0 xl:mr-8 3xl:mr-16">
            
            <img 
              src={`${process.env.PUBLIC_URL}/images/check.png`} 
              alt="Logo"
              className="w-20 h-20"   
            />

          </div>
          <div className="auth-container-box">

            <form>
              <h2 className="login-title">{isLogIn ? 'Please log in' : 'Please sign up'}</h2>

              <input 
                type="email" className="user-email"
                placeholder="Email" 
                onChange={(e) => setEmail(e.target.value)} />

              <input 
                type="password" className="user-password"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)} />
                

              {!isLogIn && <input 
                type="password" className="user-confirm-password"
                placeholder="Confirm Password" 
                onChange={(e) => setConfirmPassword(e.target.value)} />}
              
              {error && <p className="error">{error}</p>}

              <input type="submit" value={isLogIn ? "login" : 'sign up'} className="sign-in-btn"
                onClick={(e) => handleSubmit(e, isLogIn ? 'login' : 'signup')} /> 
                
              <div class="con">
                <p>
                {isLogIn ? "don't have an account?" : 'already have an account?'}&nbsp;
                <a onClick={() => (isLogIn ? viewLogin(false) : viewLogin(true))}>
                {isLogIn ? 'Sign up' : 'Sign in'}</a>
                </p>
              </div>

            </form>
          </div>
        </div>
      </div>
    );
  }
  
  export default Auth;
