import Modal from "./Modal";
import { useState } from "react";

import TickIcon from "./TickIcon";

function ListHeader({listName, getData}) {

  const [showModal, setShowModal] = useState(false);




    return (
      <div className="w-[80%] flex items-center justify-between px-6 xs:flex-col md:flex-row">
        <h1 className="flex items-center text-white font-semibold text-4xl"><TickIcon />{listName}</h1>
        <div className = "button-container">

          <button type="button" className="create-btn" onClick={() => setShowModal(true)}>
            <span class="button-text">New</span>
            <span class="button-icon"><svg xmlns="http://www.w3.org/2000/svg" width="24" viewBox="0 0 24 24"
            stroke-width="2" stroke-linejoin="round" stroke-linecap="round" stroke="currentColor" height="24" fill="none" class="svg">
            <line y2="19" y1="5" x2="12" x1="12"></line><line y2="12" y1="12" x2="19" x1="5"></line></svg></span>
          </button>


        </div>
        {showModal && <Modal mode={'create'} setShowModal = {setShowModal} getData={getData} />}
      </div>
    );
  }
  
  export default ListHeader;
