import { useCookies } from "react-cookie";

function Navbar (props) {
    
    const [cookies, setCookie, removeCookie] = useCookies(null);

    function signOut() {
        console.log("Sign Out");
        removeCookie('Email');
        removeCookie('AuthToken');
        window.location.reload();
      }

    return (
    <div className="w-full flex items-center justify-between px-6 xs:flex-col md:flex-row ">
      <div className="xs:mt-8">
        <img 
          src={`${process.env.PUBLIC_URL}/images/check.png`}
          alt="Logo"
          className="w-12 h-12"  
        />

        <p className="xs:text-sm text-gray-100 font-semibold">
          Welcome back {props.userEmail}
        </p>

        {/* Button on the right */}
        <button type="button" className="signout-btn ml-4" onClick={signOut}>
          Sign out
        </button>
      </div>
    </div>

    
    );
}

export default Navbar;
