import { useState } from "react";
import { useCookies } from 'react-cookie';

function Auth() {
  const[cookies,setCookie, removeCookie] = useCookies(null);

  const [isLogIn, setIsLogin] = useState(true);

  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirmPassword, setConfirmPassword] = useState(null);

  const [error, setError] = useState(null);

  console.log(cookies);

  function viewLogin(status) {
    setError(null);
    setIsLogin(status);
  }

  async function handleSubmit(e, endpoint) {
    e.preventDefault();

    if(!isLogIn && password !== confirmPassword) {
      setError('Make sure passwords match!');
      return
    }

    const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/${endpoint}`, {
      method: 'POST',
      headers: {"Content-Type": 'application/json'},
      body: JSON.stringify({email, password})
    });

    const data = await response.json();
    console.log(data);

    if(data.detail) {
      setError(data.detail);
    } else {
      setCookie('Email', data.email);
      setCookie('AuthToken', data.token);

      window.location.reload();
    }
  }

    return (
    

    <div className = "auth-container">
      <div className="auth-container-box">  
        <form class="form_container">

          <div class="title_container">
            <p class="title">{isLogIn ? 'Please log in' : 'Please sign up'}</p>
          </div>  
        
          <br />

          <label class="label">Email</label>
          <input 
            placeholder="Email" class="input" type="email" 
            onChange={(e) => setEmail(e.target.value)} required
          />

          <label class="label">Password</label>
          <input 
            placeholder="Password" class="input" type="password" 
            onChange={(e) => setPassword(e.target.value)} required
          />

          {!isLogIn && 
          <div>

            <label class="label">Password</label>
            <input 
              placeholder="confirm password" class="input" type="password" 
              onChange={(e) => setConfirmPassword(e.target.value)} required
            />

          </div>}
        
          <button title="Sign In" type="submit" class="sign-in_btn"
            onClick={(e) => handleSubmit(e, isLogIn ? 'login' : 'signup')}>
            <span>Sign In</span>
          </button>

          {error && <p>{error}</p>}

        </form>
      </div>  
    </div>
    );
  }
  
  export default Auth;
