import Modal from "./Modal";
import { useState } from "react";
import { useCookies } from "react-cookie";
import TickIcon from "./TickIcon";

function ListHeader({listName, getData}) {

  const [showModal, setShowModal] = useState(false);
  const [cookies, setCookie, removeCookie] = useCookies(null);

  function signOut() {
    console.log("Sign Out");
    removeCookie('Email');
    removeCookie('AuthToken');
    window.location.reload();
  }

    return (
      <div className="list-header">
        <h1 className="list-name"><TickIcon />{listName}</h1>
        <div className = "button-container">

          <button type="button" className="create-btn" onClick={() => setShowModal(true)}>
            <span class="button-text">New</span>
            <span class="button-icon"><svg xmlns="http://www.w3.org/2000/svg" width="24" viewBox="0 0 24 24"
            stroke-width="2" stroke-linejoin="round" stroke-linecap="round" stroke="currentColor" height="24" fill="none" class="svg">
            <line y2="19" y1="5" x2="12" x1="12"></line><line y2="12" y1="12" x2="19" x1="5"></line></svg></span>
          </button>

          <button type="button" className="signout-btn" onClick={signOut}>
            <span class="button-text">Sign out</span>
            <span class="button-icon"><svg xmlns="http://www.w3.org/2000/svg" width="16" viewBox="0 0 512 512">
            <path d="M502.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-12.5-12.5-32.8-12.5-45.3 0s-12.5 
            32.8 0 45.3L402.7 224 192 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l210.7 0-73.4 73.4c-12.5 
            12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0l128-128zM160 96c17.7 0 32-14.3 32-32s-14.3-32-32-32L96 
            32C43 32 0 75 0 128L0 384c0 53 43 96 96 96l64 0c17.7 0 32-14.3 32-32s-14.3-32-32-32l-64 0c-17.7 
            0-32-14.3-32-32l0-256c0-17.7 14.3-32 32-32l64 0z"/></svg></span>
          </button>

        </div>
        {showModal && <Modal mode={'create'} setShowModal = {setShowModal} getData={getData} />}
      </div>
    );
  }
  
  export default ListHeader;
