import { useCookies } from "react-cookie";

function Navbar (props) {
    
    const [cookies, setCookie, removeCookie] = useCookies(null);

    function signOut() {
        console.log("Sign Out");
        removeCookie('Email');
        removeCookie('AuthToken');
        window.location.reload();
      }

    return (
    <div className="w-full flex items-center justify-between px-6 xs:flex-col md:flex-row">
    
        <div className="flex items-center justify-center xs:flex-col md:flex-row">
          <img 
            src={`${process.env.PUBLIC_URL}/images/check.png`}
            alt="Logo"
            className="xs:w-16 xs:h-16 xs:mt-8 md:w-12 md:h-12 md:mt-6"  
          />
          <div className="text-2xl text-gray-100 font-semibold md:mt-6 md:ml-3 xs:mt-1">Listly</div>
          
        </div>

        <p className="xs:text-sm xs:mt-4 md:mt-6 text-gray-100 font-medium">
          Welcome back {props.userEmail}
        </p>

        {/* Button on the right */}
        <button type="button" className="text-white mt-6 bg-red-500 p-2 font-semibold text-md hover:text-red-700" onClick={signOut}>
          Sign out
        </button>
      
    </div>

    
    );
}

export default Navbar;
