import { useCookies } from "react-cookie";
import TickIcon from "./TickIcon";

function Navbar ({props ,listName}) {
    
    const [cookies, setCookie, removeCookie] = useCookies(null);

    function signOut() {
        console.log("Sign Out");
        removeCookie('Email');
        removeCookie('AuthToken');
        window.location.reload();
      }

    return (
      <div className="border-b border-b-[#252525] relative w-full bg-[#191919] flex items-center justify-between px-6 py-5 xs:flex-col md:flex-row">
      <div className="flex items-center xs:mb-4 md:mb-0">
        <img 
          src={`${process.env.PUBLIC_URL}/images/check.png`}
          alt="Logo"
          className="xs:w-16 xs:h-16 md:w-8 md:h-8"  
        />
        <div className="text-2xl text-gray-100 font-semibold md:ml-3">Listly</div>
      </div>

      <h1 className="absolute left-1/2 transform -translate-x-1/2 text-white font-bold text-3xl flex items-center">
        <TickIcon />To Do list
      </h1>

      {/* <p className="xs:text-sm text-gray-100 font-medium">
          Welcome back {props.userEmail}
        </p> */}
    
      <button 
        type="button" 
        className="text-white bg-red-600 p-2 rounded-md font-semibold text-sm hover:bg-red-700" 
        onClick={signOut}
      >
        Sign out
      </button>
    </div>
    

    
    );
}

export default Navbar;
