import { useCookies } from "react-cookie";
import TickIcon from "./TickIcon";
{listName, getData}


function Navbar (props) {
    
    const [cookies, setCookie, removeCookie] = useCookies(null);

    function signOut() {
        console.log("Sign Out");
        removeCookie('Email');
        removeCookie('AuthToken');
        window.location.reload();
      }

    return (
    <div className="w-full bg-[#151515] flex items-center justify-between px-6 py-5 xs:flex-col md:flex-row">
    
        <div className="flex items-center justify-center xs:flex-row">
          <img 
            src={`${process.env.PUBLIC_URL}/images/check.png`}
            alt="Logo"
            className="xs:w-16 xs:h-16 md:w-10 md:h-10"  
          />
          <div className="text-2xl text-gray-100 font-semibold md:ml-3">Listly</div>
          
        </div>
        <h1 className="flex items-center text-white font-bold text-4xl"><TickIcon />{listName}</h1>


        {/* <p className="xs:text-sm text-gray-100 font-medium">
          Welcome back {props.userEmail}
        </p> */}

        {/* Button on the right */}
        <button type="button" className="text-white  bg-red-600 p-2 rounded-md font-semibold text-sm hover:bg-red-700" onClick={signOut}>
          Sign out
        </button>
      
    </div>

    
    );
}

export default Navbar;
