import { useState } from "react";
import { useCookies } from "react-cookie";

function Modal({mode, setShowModal, getData, task}) {
  const [cookies, setCookie, removeCookie] = useCookies(null);

  const editMode = mode === 'edit' ? true : false;

  const [data, setData] = useState({
    user_email: editMode ? task.user_email : cookies.Email,
    title: editMode ? task.title : "",
    progress: editMode ? task.progress : 50,
    date: editMode ? task.date : new Date()
  });

  async function postData(e) {
    e.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/todos`, {
        method: "POST",
        headers: { 'Content-Type': 'application/json'},
        body: JSON.stringify(data)
      });

      if (response.status === 200) {
        console.log("WORKED");
        setShowModal(false);
        getData();
      }
      
    } catch (err) {
      console.error(err);
    }
  }

  async function editData(event) {
    event.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/todos/${task.id}`, {
        method: "PUT",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });

      if (response.status === 200) {
        setShowModal(false);
        getData();
      } 

    } catch (err) {
      console.error(err);
    }
  }





  function handleChange(event) {
    const { name, value } = event.target;
    
    setData(data => ({
      ...data,
      [name]: value
    }))

    // console.log(data);
  }

    return (
      <div className="overlay">
        <div className="modal">
          <div className="form-title-container">
            <h3>Let's {mode} your task</h3>
            <button className="close-button"
            onClick = {() => setShowModal(false)}>X</button>
          </div>
          <form>
            <input 
              required
              className="task-input" 
              maxLength = {30}
              placeholder = "Your task..."
              name = "title"
              value = {data.title}
              onChange = {handleChange}  
            />
            <br/>

            <label htmlFor="range">Drag to select your current progress</label>

            <input
              required
              className="task-range"
              type = "range"
              id = "range"
              min = "10"
              max = "100"
              name = "progress"
              value = {data.progress}
              onChange = {handleChange}
            />

            <input className={mode} type="submit" onClick={editMode ? editData : postData}/>
          </form>

        </div>
      </div>
    );
  }
  
  export default Modal;
