import { useCookies } from "react-cookie";

function Navbar (props) {
    
    const [cookies, setCookie, removeCookie] = useCookies(null);

    function signOut() {
        console.log("Sign Out");
        removeCookie('Email');
        removeCookie('AuthToken');
        window.location.reload();
      }

    return (
    <div className="w-[100%] flex flex-row">
      <div className="mt-6 pl-6 flex items-center xs:mt-6 xs:pl-6 space-x-4">

        <img 
          src={`${process.env.PUBLIC_URL}/images/check.png`}
          alt="Logo"
          className="w-12 h-12 xs:w-12 xs:h-12"  
        />

        <p className="text-4xl ml-4 text-gray-100 font-semibold xs:ml-4 xs:text-4xl">Welcome back {props.userEmail}</p>


        <button type="button" className="signout-btn" onClick={signOut}>Sign out</button>
      </div>
          
    </div>
    );
}

export default Navbar;
