import Modal from "./Modal";
import { useState } from "react";
import { useCookies } from "react-cookie";

function ListHeader({listName, getData}) {

  const [showModal, setShowModal] = useState(false);
  const [cookies, setCookie, removeCookie] = useCookies(null);

  function signOut() {
    console.log("Sign Out");
    removeCookie('Email');
    removeCookie('AuthToken');
    window.location.reload();
  }

    return (
      <div className="list-header">
        <h1>{listName}</h1>
        <div className = "button-container">

          <button type="button" className="create-btn" onClick={() => setShowModal(true)}><span class="button-text">Add Item</span>
            <span class="button-icon"><svg xmlns="http://www.w3.org/2000/svg" width="24" viewBox="0 0 24 24"
            stroke-width="2" stroke-linejoin="round" stroke-linecap="round" stroke="currentColor" height="24" fill="none" class="svg">
            <line y2="19" y1="5" x2="12" x1="12"></line><line y2="12" y1="12" x2="19" x1="5"></line></svg>
            </span></button>

          <button className="signout" onClick={signOut}>Sign out</button>
        </div>
        {showModal && <Modal mode={'create'} setShowModal = {setShowModal} getData={getData} />}
      </div>
    );
  }
  
  export default ListHeader;
